var Marr = msg.payload.Marr;
var Marl = msg.payload.Marl;
var MTime = msg.payload.MTime;
const gHomeAssistant = global.get('homeassistant').homeAssistant;

// --------------------------------------------------




//Edit these to match your media player
var CurPos = gHomeAssistant.states['media_player.kodi'].attributes.media_position;
var MPState = gHomeAssistant.states['media_player.kodi'].state;


//Edit this to match your light if you dont use this group
var LiteColor = gHomeAssistant.states['light.movie_color_group'].attributes.rgb_color;





// --------------------------------------------------

//Status Message
var StatMsg = MPState + " time: " + CurPos;
var StatClr = "green";


// if the time hasnt changed do nothing
var NewColor = 'none';

if ((MTime != CurPos) && (LiteColor !== undefined)) {

    var CI = 0;
    var i = 0;
    var R1 = Marr[i].R;
    var G1 = Marr[i].G;
    var B1 = Marr[i].B;     

    while (i < Marl) {
      CI = Marr[i].MPos;
      if (CI <= CurPos) {

        R1 = Marr[i].R;
        G1 = Marr[i].G;
        B1 = Marr[i].B;    

        i++;

      } else {
        break;
      }
    }


// If the old color was almost the same do nothing.

    if ((LiteColor[0]+4 > R1) && (LiteColor[1]+4 > G1) && (LiteColor[2]+4 > B1) && (LiteColor[0]-4 < R1) && (LiteColor[1]-4 < G1) && (LiteColor[2]-4 < B1)) {

        NewColor = 'none';

        // Stop when template is complete

        if (i == (Marl)) {
            NewColor = 'stop';
            StatMsg = "Template Script Complete";
            StatClr = "blue";
        }

    } else {

        NewColor = '[' + R1 + ',' + G1 + ',' + B1 + ']';

    }

}



// If media player isn't playing anymore we are done

if (MPState != 'playing') {

    NewColor = 'stop';
    StatMsg = MPState;
    StatClr = "yellow";

}


msg.payload = {color: NewColor,
               MTime: CurPos,
               Marl: Marl,
               Marr: Marr,
               OldColor: LiteColor,
               MPState: MPState
};


node.status({fill:StatClr,shape:"dot",text:StatMsg});

return msg;

var Marl = msg.payload.length;
var Marr = [...msg.payload];
Marr.sort(function(a, b) { return a.MPos - b.MPos });

msg.payload = {
    Marl: Marl,
    Marr: Marr
};

return msg;
